下面是完整的代码实现：


```c
#include <stdio.h>
#include <stdlib.h>
#include <time.h>

// 磁盘总磁道数
#define TRACK_NUM 256

// 磁头当前所在磁道
#define CURRENT_TRACK 100

// 磁盘I/O请求个数
#define REQUEST_NUM 10

// 定义磁盘I/O任务结构体
typedef struct {
    int track_num;      // 磁道号
    int arrive_time;    // 到达时间
} io_request;

// 全局变量：磁盘I/O任务数组
io_request io_requests[REQUEST_NUM];

// 计算平均寻道长度函数
int calculate_average_seek_time(int start_track, io_request *requests, int request_num,
                                int (*scanner)(int*, int, io_request*, int), const char* method_name) {
    int current_track = start_track;        // 初始磁道
    int total_seek_time = 0;                // 总寻道长度
    int i;
    printf("%s:\n", method_name);
    printf("起始磁道：%d\n", start_track);
    printf("磁道序列：");
    for (i = 0; i < request_num; i++) {
        int served_track = scanner(&current_track, TRACK_NUM, requests, request_num);
        int seek_time = abs(current_track - served_track);
        total_seek_time += seek_time;
        current_track = served_track;
        printf("%d ", served_track);
    }
    printf("\n");
    return total_seek_time / request_num;
}

// FCFS算法
int fcfs(int *current_track, int track_num, io_request *requests, int request_num) {
    int served_track = requests[0].track_num;
    return served_track;
}

// SSTF算法
int sstf(int *current_track, int track_num, io_request *requests, int request_num) {
    int i, min_distance = TRACK_NUM, min_index = 0;
    for (i = 0; i < request_num; i++) {
        int distance = abs(*current_track - requests[i].track_num);
        if (distance < min_distance) {
            min_distance = distance;
            min_index = i;
        }
    }
    int served_track = requests[min_index].track_num;
    for (i = min_index; i < request_num - 1; i++) {
        requests[i] = requests[i + 1];
    }
    return served_track;
}

// SCAN算法
int scan(int *current_track, int track_num, io_request *requests, int request_num) {
    int i, j;
    int direction = (*current_track <= TRACK_NUM / 2) ? 1 : -1;   // 设置初始移动方向
    int served_track = TRACK_NUM;       // 从外部一侧的最大磁道开始扫描
    for (i = 0; i < TRACK_NUM; i++) {
        int next_track = *current_track + direction * i;
        if (next_track >= 0 && next_track < TRACK_NUM) {
            for (j = 0; j < request_num; j++) {
                if (requests[j].track_num == next_track) {
                    served_track = next_track;
                    goto stop_scan;     // 找到最近的磁道，停止SCAN扫描
                }
            }
        }
    }
    stop_scan:
    for (i = 0; i < request_num; i++) {  // 从数组中删除已服务的磁道号
        if (requests[i].track_num == served_track) {
            for (j = i; j < request_num - 1; j++) {
                requests[j] = requests[j + 1];
            }
            break;
        }
    }
    return served_track;
}

// CSCAN算法
int cscan(int *current_track, int track_num, io_request *requests, int request_num) {
    int i, j;
    int served_track = TRACK_NUM;       // 从最外侧的最大磁道开始扫描
    int direction = (*current_track <= TRACK_NUM / 2) ? 1 : -1;  // 设置初始移动方向
    for (i = 0; i < TRACK_NUM; i++) {
        int next_track = *current_track + direction * i;        // 扫描下一个磁道
        if (next_track < 0 || next_track >= TRACK_NUM) {        // 扫描到磁盘左右端口后，修改移动方向
            direction = -direction;
            next_track = *current_track + direction;
        }
        for (j = 0; j < request_num; j++) {
            if (requests[j].track_num == next_track) {
                served_track = next_track;
                goto stop_scan;
            }
        }
    }
    stop_scan:
    for (i = 0; i < request_num; i++) {  // 从数组中删除已服务的磁道号
        if (requests[i].track_num == served_track) {
            for (j = i; j < request_num - 1; j++) {
                requests[j] = requests[j + 1];
            }
            break;
        }
    }
    return served_track;
}

int main() {
    int i;
    srand((unsigned int) time(NULL));  // 初始化随机数种子
    printf("随机生成的磁道I/O请求序列：\n");
    for (i = 0; i < REQUEST_NUM; i++) {
        io_requests[i].track_num = rand() % TRACK_NUM;
        io_requests[i].arrive_time = i;
        printf("%d ", io_requests[i].track_num);
    }
    printf("\n");

    // 计算寻道时间
    printf("\n计算平均寻道时间：\n");
    int fcfs_seek_time = calculate_average_seek_time(CURRENT_TRACK, io_requests, REQUEST_NUM, fcfs, "FCFS");
    printf("平均寻道长度（FCFS）：%d\n", fcfs_seek_time);
    int sstf_seek_time = calculate_average_seek_time(CURRENT_TRACK, io_requests, REQUEST_NUM, sstf, "SSTF");
    printf("平均寻道长度（SSTF）：%d\n", sstf_seek_time);
    int scan_seek_time = calculate_average_seek_time(CURRENT_TRACK, io_requests, REQUEST_NUM, scan, "SCAN");
    printf("平均寻道长度（SCAN）：%d\n", scan_seek_time);
    int cscan_seek_time = calculate_average_seek_time(CURRENT_TRACK, io_requests, REQUEST_NUM, cscan, "CSCAN");
    printf("平均寻道长度（CSCAN）：%d\n", cscan_seek_time);

    return 0;
}
